import React, { useState } from 'react';
import './CreateTicketsFile.css'

function CreateTicketsFile() {
  // Define state variables at the top level of the component
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState(''); // Define error state at the top level
  const [success, setSuccess] = useState(''); // Define success state at the top level

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const ticketData = {
      amount: parseFloat(amount),
      description: description,
      status: "Pending",
    };

    try {
      const response = await fetch('http://localhost:8080/tickets/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(ticketData),
      });

      if (response.ok) {
        const result = await response.json();
        setSuccess(`Ticket submitted successfully! Ticket ID: ${result.id}`);
        setError('');
      } else {
        const errorText = await response.text();
        setError(`Error: ${errorText}`);
        setSuccess(''); 
      }
    } catch (error) {
      setError('Error: Unable to submit the ticket.');
      setSuccess(''); 
    }
  };

  return (
    <div className='create-ticket-container'>
      <h2>Create a Ticket</h2>
      <form className='create-form' onSubmit={handleSubmit}>
        <div>
          <label id='amount'>Amount: </label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label id='description'>Description: </label>
          <input
            type='text'
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <button type="submit">Submit Ticket</button>
      </form>

      {}
      {success && <p id="success">{success}</p>}
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default CreateTicketsFile;
