import React, { useState } from 'react';
import './CreateTicketsFile.css'

function CreateTicketsFile() {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = async (e: { preventDefault: () => void; }) => {
    e.preventDefault();

    const ticketData = {
      amount: parseFloat(amount),
      description: description,
      status: "Pending",
    };

      const [error, setError] = useState('');
      const [success, setSuccess] = useState('')

    try {
      const response = await fetch('http://localhost:8080/tickets/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(ticketData),
      });

      if (response.ok) {
        const result = await response.json();
        setSuccess(`Ticket submitted successfully! Ticket ID: ${result.id}`);
        return
      } else {
        const error = await response.text();
        setMessage(`Error: ${error}`);
      }
    } catch (error) {
      setMessage('Error: Unable to submit the ticket.');
    }
  };

  return (
    <div className='create-ticket-container'>
      <h2>Create a Ticket</h2>
      <form className='create-form' onSubmit={handleSubmit}>
        <div>
          <label id='amount'>Amount: </label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label id='description'>Description: </label>
          <input type='text'
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <button type="submit">Submit Ticket</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default CreateTicketsFile;
