import React, { useState } from 'react';

function CreateTicketsFile() {
  const [formData, setFormData] = useState({
    description: '',
    amount: '',
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e: { target: { name: any; value: any; }; }) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e: { preventDefault: () => void; }) => {
    e.preventDefault();

    if (!formData.description || !formData.amount) {
      setError('All fields are required.');
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/tickets/submit', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setSuccess('Ticket has been submitted');
        setError('');
        setFormData({ description: '', amount: '' });
      } else {
        const errorMessage = await response.text();
        setError(errorMessage);
        setSuccess('');
      }
    } catch (err) {
      setError('An error occurred while submitting the ticket. Please try again.');
      setSuccess('');
    }
  };

  return (
    <div className='register-container'>
      <h3>Create a Ticket</h3>
      <form className='register-form' onSubmit={handleSubmit}>
        {error && <div className="error">{error}</div>}
        {success && <div className="success">{success}</div>}
        <label className='description'>
          Description
          <input
            type='text'
            name='description'
            value={formData.description}
            onChange={handleChange}
          />
        </label>
        <label className='amount'>
          Amount
          <input
            type='text'
            name='amount'
            value={formData.amount}
            onChange={handleChange}
          />
        </label>
        <button type='submit'>Submit</button>
      </form>
    </div>
  );
}

export default CreateTicketsFile;
